DROP TABLE IF EXISTS `vote_ctrl`;
CREATE TABLE `vote_ctrl` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL COMMENT '参选者ID',
